<template>
  <div>
    <Card size="small">
      <div class="lycard-left">
        <span :class="iconClass" style="width: 20px"> :</span>
        <span style="margin-left: 20px; width: 40%">{{ monitorData.system }}</span>
        <span style="margin-left: 20px; width: 20%">
          {{ t('common.monitor.runTimeText') }}: {{ monitorData.time }}</span
        >
        <span style="margin-left: 20px; width: 10%"
          >{{ t('common.monitor.autoRefreshText') }}：</span
        >
        <InputNumber
          v-model:value="refreshInterval"
          size="small"
          style="width: 20%"
          :min="3"
          @change="restartIntervalMonitor"
        />
        <a-button
          style="margin-left: 20px"
          type="link"
          v-if="timer"
          :text="true"
          link
          @click="getData"
          ><span style="font-size: 13px" @click="clearIntervalMonitor">{{
            t('common.monitor.stopText')
          }}</span></a-button
        >
        <a-button
          style="margin-left: 20px"
          type="link"
          v-if="timer == null"
          :text="true"
          link
          @click="getData"
        >
          <span style="font-size: 13px" @click="restartIntervalMonitor">{{
            t('common.monitor.startText')
          }}</span>
        </a-button>
        <a-button type="link" :text="true" link @click="getData"
          ><span style="font-size: 13px">
            {{ t('common.monitor.manualRefreshText') }}</span
          ></a-button
        >
      </div>
    </Card>
    <div>
      <ly-statuscard v-model="monitorData" class="!my-4 enter-y" />
    </div>
    <div>
      <EchartCard :network="monitorData.network" class="!my-4 enter-y" />
    </div>
  </div>
</template>

<script>
  import { InputNumber, Card } from 'ant-design-vue';
  import LyStatuscard from './component/statusCard.vue';
  import { getSystemInfo } from './api';
  import EchartCard from './component/echartCard.vue';
  import { useI18n } from '../../../../hooks/web/useI18n';

  export default {
    name: 'Server',
    components: { EchartCard, LyStatuscard, InputNumber, Card },
    data() {
      return {
        t: undefined,
        isFull: false,
        isRunning: false,
        showloading: true,
        tableHeight: '500px',
        monitorData: {
          cpu: [0, 0, [0, 0, 0, 0], '', 0, 1],
          disk: [{ path: '', size: ['0GB', '0GB', '0GB', 0], inodes: false }],
          is_windows: true,
          load_average: { one: 0, five: 0, fifteen: 0, max: 0, limit: 0, safe: 0, percent: 0 },
          mem: { percent: 0, total: 0, free: 0, used: 0 },
          system: '',
          time: '0day',
          network: {
            up: 0,
            down: 0,
            downTotal: 0,
            upTotal: 0,
            network: {},
          },
        },
        refreshInterval: 3,
        iconClass: '',
        timer: null, //定时器
      };
    },
    watch: {
      isFull: function () {
        this.listenResize();
      },
    },
    created() {
      this.getData();
      const { t } = useI18n();
      this.t = t;
    },
    mounted() {
      if (!this.isRunning) {
        this.intervalMonitor();
        this.isRunning = true;
        setTimeout(() => {
          this.showloading = false;
        }, 2000);
      }
      // 监听页面宽度变化搜索框的高度
      window.addEventListener('resize', this.listenResize);
      this.listenResize();
    },
    activated() {
      if (!this.isRunning) {
        this.intervalMonitor();
        this.isRunning = true;
      }
    },
    deactivated() {
      this.isRunning = false;
      this.clearIntervalMonitor();
    },
    unmounted() {
      this.clearIntervalMonitor();
      // 页面销毁，去掉监听事件
      window.removeEventListener('resize', this.listenResize);
    },
    methods: {
      setFull() {
        this.isFull = !this.isFull;
      },
      //获取列表
      getData() {
        getSystemInfo().then((res) => {
          this.monitorData = res;
          let tempsystem = res.system.split(' ')[0].toLowerCase();
          this.iconClass = 'ico-' + tempsystem;
        });
      },
      intervalMonitor() {
        // eslint-disable-next-line @typescript-eslint/no-this-alias
        let that = this;
        this.timer = setInterval(() => {
          that.getData();
        }, that.refreshInterval * 1000);
      },
      restartIntervalMonitor() {
        this.clearIntervalMonitor();
        this.intervalMonitor();
      },
      clearIntervalMonitor() {
        clearInterval(this.timer);
        this.timer = null;
      },
      handleResize() {},
      // 计算搜索栏的高度
      listenResize() {},
      getTheTableHeight() {},
    },
  };
</script>

